__author__ = 'bkurniawan'from rl.agent.base_agent import BaseAgent
#from .. import rl_utilsclass ControllableAgent(BaseAgent):    """    This class represents an opposing agent that can be controlled through a json file.    """    def __init__(self, params_filename=None):        super(ControllableAgent, self).__init__()        self.action = 'no_command()' # set this so tick() can be called without first calling set_action()    def set_action(self, action):        self.action = action            def tick(self, t, dt):        super().tick(t, dt) # must call super to log and empty commands        exec('self.' + self.action)