__author__ = 'bkurniawan'from random import randintfrom rl.agent.base_agent import BaseAgent
from .. import rl_utilsclass RandomAgent(BaseAgent):    """    This class represents an opposing agent that can be controlled through a json file.    """    def __init__(self, params_filename=None):        super(RandomAgent, self).__init__()        if params_filename:            self.actions = params_filename['actions'] if 'actions' in params_filename else None            self.interval = int(params_filename['interval']) if 'interval' in params_filename else None    def tick(self, t, dt):        self.commands = []        if self.actions and self.interval and t % self.interval == 0:            random_number = randint(0, len(self.actions) - 1)            action = self.actions[random_number]            exec('self.' + action)